	proc rotaX { rotx } {
		global ::Aminoacidos::varx
		if { $varx == $rotx  } {
			puts "ZERO...$varx $rotx" }
		if { $varx < $rotx  } {
			set varx $rotx
			rotate x by 2  }
		if { $varx > $rotx  } {
			set varx $rotx
			rotate x by -2 }
	}
	proc rotaY { roty } {
		global ::Aminoacidos::vary
		if { $vary == $roty  } {
			puts "ZERO...$vary $roty" }
		if { $vary < $roty  } {
			set vary $roty
			rotate y by 2  }
		if { $vary > $roty  } {
			set vary $roty
			rotate y by -2 }
	}
	proc rotaZ { rotx } {
		global ::Aminoacidos::varx
		if { $varx == $rotx  } {
			puts "ZERO...$varx $rotx" }
		if { $varx < $rotx  } {
			set varx $rotx
			rotate z by 2  }
		if { $varx > $rotx  } {
			set varx $rotx
			rotate z by -2 }
	}
	proc autorotY { } {
		global rockY
		if { $rockY == 1 } { rock y by 0.15 }
		if { $rockY == 0 } { rock off     } }

	proc resize { zoomv }	{ scale to  [expr 0.01 * $zoomv] }

  #####################
  ## REPRESENTATION PROCS
  #####################

	proc carep {cav} {
#		global cav
		if { $cav == 1 } { mol showrep top 1 on }
		if { $cav == 0 } { mol showrep top 1 off } }

	proc coorep {coov} {
#		global coov
		if { $coov == 1 } { mol showrep top 2 on }
		if { $coov == 0 } { mol showrep top 2 off } }

	proc nh3rep {nh3v} {
#		global nh3v
		if { $nh3v == 1 } { mol showrep top 3 on }
		if { $nh3v == 0 } { mol showrep top 3 off } }

	proc siderep {sidev} {
#		global sidev
		if { $sidev == 1 } { mol showrep top 4 on }
		if { $sidev == 0 } { mol showrep top 4 off } }

	proc harep {hav} {
#		global hav
		if { $hav == 1 } { mol showrep top 5 on }
		if { $hav == 0 } { mol showrep top 5 off } }

	proc sdrep {sdrep} {
#		global sdv
		if { $sdv == 1 } { mol showrep top 6 on }
		if { $sdv == 0 } { mol showrep top 6 off } }


